(#{one.threadId}, #{one.title}, #{one.gameVersion}, #{one.views}, #{one.likes}, #{one.likeRatio}, #{one.rating}, #{one.gameUpdateDate}, CURRENT_TIMESTAMP, CURRENT_TIMESTAMP, 'RELEASED', #{one.extInfo})
ON DUPLICATE KEY UPDATE title = values(title), game_version = values(game_version), likes = values(likes), like_ratio = values(like_ratio), rating = values(rating), game_update_date = values(game_update_date), gmt_modified = CURRENT_TIMESTAMP, ext_info = values(ext_info);
